pragma solidity ^0.4.24;

import "./ERC20Burnable.sol";
import "./ERC20Mintable.sol";
import "./ERC20Detailed.sol";

/**
 * @title Mintable Burnable Token
 * @dev Token that can be both created and burned by its owner.
 */
contract ERC20MintableBurnable is ERC20Mintable, ERC20Burnable, ERC20Detailed {
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    constructor (string name, string symbol, uint8 decimals, uint256 initialValue) public {
      _name = name;
      _symbol = symbol;
      _decimals = decimals;
      _mint(msg.sender, initialValue);
    }
}
